import{load_image,unload_image,load_json,insert,remove}from"/4500-2021-spring/static/utils.js";import{c_frame}from"/4500-2021-spring/static/c_frame.js";export function c_sprites(t){this.room=null,this.name=t,this.image=null,this.json_frames=null}c_sprites.prototype.load=function(){return Promise.all([load_image(`/4500-2021-spring/sprites/${this.name}.png`),load_json(`/4500-2021-spring/sprites/${this.name}.json`)]).then((([t,s])=>{this.image=t,this.json_frames=s}))},c_sprites.prototype.unload=function(){unload_image(this.image)},c_sprites.prototype.once=function(t){return new c_frame(this,t)},c_sprites.prototype.loop=function(t){const s=new c_frame(this,t);return s.loop=!0,s};