export function starts(t){return this.start_set?this.start_set.push(t):this.start_set=[t],this}export function stops(t){return this.stop_set?this.stop_set.push(t):this.stop_set=[t],this}export function run_stop_set(){"stop_set"in this&&this.stop_set.forEach((t=>t.stop()))}export function run_start_set(){"start_set"in this&&this.start_set.forEach((t=>{"string"==typeof t?this.room.goto(t):t.start()}))}